'use strict';

const path = require('path');
const through2 = require('through2-concurrent');
const gutil = require('gulp-util');
const filesize = require('filesize');
const optimize = require('./optimize');
const round10 = require('./round10');

module.exports = options => through2.obj({
  maxConcurrency: options ? options.concurrent : null
}, (file, enc, callback) => {
  if (file.isNull()) {
    return callback(null, file);
  }

  if (file.isStream()) {
    return callback(new Error('gulp-image: Streaming is not supported'));
  }

  const extension = path.extname(file.path).toLowerCase();

  if (['.jpg', '.jpeg', '.png', '.gif', '.svg'].indexOf(extension) === -1) {
    gutil.log('gulp-image: Skipping unsupported image ' + gutil.colors.blue(file.relative));
    return callback(null, file);
  }

  optimize(file.contents, Object.assign({
    pngquant       : true,
    optipng        : false,
    zopflipng      : true,
    jpegRecompress : false,
    jpegoptim      : true,
    mozjpeg        : true,
    guetzli        : false,
    gifsicle       : true,
    svgo           : true
  }, options)).then(buffer => {
    const before = file.contents.length;
    const after = buffer.length;
    const diff = before - after;
    const diffPercent = round10(100 * (diff / before), -1);

    if (diff <= 0) {
      gutil.log(
        gutil.colors.green('- ') + file.relative + gutil.colors.gray(' ->') +
        gutil.colors.gray(' Cannot improve upon ') + gutil.colors.cyan(filesize(before))
      );
    } else {
      file.contents = buffer;

      gutil.log(
        gutil.colors.green('✔ ') + file.relative + gutil.colors.gray(' ->') +
        gutil.colors.gray(' before=') + gutil.colors.yellow(filesize(before)) +
        gutil.colors.gray(' after=') + gutil.colors.cyan(filesize(after)) +
        gutil.colors.gray(' reduced=') + gutil.colors.green.underline(filesize(diff) + '(' + diffPercent + '%)')
      );
    }

    callback(null, file);
  }).catch(error => {
    callback(new gutil.PluginError('gulp-image', error));
  });
});

'use strict';

const path = require('path');
const execa = require('execa');
const execBuffer = require('exec-buffer');
const isPng = require('is-png');
const isJpg = require('is-jpg');
const isGif = require('is-gif');
const isSvg = require('is-svg');

const optipng = buffer => execBuffer({
  input : buffer,
  bin   : require('optipng-bin'),
  args  : [
    '-i 1',
    '-strip all',
    '-fix',
    '-o7',
    '-force',
    '-out', execBuffer.output,
    execBuffer.input
  ]
});

const pngquant = buffer => execa.stdout(require('pngquant-bin'), [
  '--speed=1',
  '--force',
  '256'
], {
  encoding : null,
  input    : buffer
});

const pngcrush = buffer => execBuffer({
  input : buffer,
  bin   : require('pngcrush-bin'),
  args  : [
    '-rem alla',
    '-rem text',
    '-brute',
    '-reduce',
    execBuffer.input,
    execBuffer.output
  ]
});

const zopflipng = buffer => execBuffer({
  input : buffer,
  bin   : require('zopflipng-bin'),
  args  : [
    '-y',
    '--lossy_8bit',
    '--lossy_transparent',
    execBuffer.input,
    execBuffer.output
  ]
});

const gifsicle = buffer => execBuffer({
  input : buffer,
  bin   : require('gifsicle'),
  args  : [
    '--optimize',
    '--output',
    execBuffer.output,
    execBuffer.input
  ]
});

const jpegRecompress = buffer => execBuffer({
  input : buffer,
  bin   : require('jpeg-recompress-bin'),
  args  : [
    '--strip',
    '--quality', 'medium',
    '--min', 40,
    '--max', 80,
    execBuffer.input,
    execBuffer.output
  ]
});

const jpegoptim = buffer => execa.stdout(require('jpegoptim-bin'), [
  '--strip-all',
  '--strip-iptc',
  '--strip-icc',
  '--stdin',
  '--stdout'
], {
  encoding : null,
  input    : buffer
});

const mozjpeg = buffer => execa.stdout(require('mozjpeg'), [
  '-optimize',
  '-progressive'
], {
  encoding : null,
  input    : buffer
});

const guetzli = buffer => execBuffer({
  input : buffer,
  bin   : require('guetzli'),
  args  : [
    '--quality', 85,
    execBuffer.input,
    execBuffer.output
  ]
});

const svgo = (buffer, options) => {
  const args = [
    '--input', execBuffer.input,
    '--output', execBuffer.output
  ];

  if (Array.isArray(options.enables)) {
    options.enables.forEach(enable => {
      args.push(`--enable=${enable}`);
    });
  }

  if (Array.isArray(options.disables)) {
    options.disables.forEach(disable => {
      args.push(`--disable=${disable}`);
    });
  }

  return execBuffer({
    input : buffer,
    bin   : path.join(path.dirname(require.resolve('exec-buffer')), '../svgo/bin/svgo'),
    args  : args
  });
}

module.exports = (buffer, options) => {
  if (isJpg(buffer)) {
    return Promise.resolve(buffer)
      .then(buffer => options.jpegRecompress ? jpegRecompress(buffer) : buffer)
      .then(buffer => options.jpegoptim ? jpegoptim(buffer) : buffer)
      .then(buffer => options.mozjpeg ? mozjpeg(buffer) : buffer)
      .then(buffer => options.guetzli ? guetzli(buffer) : buffer);
  } else if (isPng(buffer)) {
    return Promise.resolve(buffer)
      .then(buffer => options.pngquant ? pngquant(buffer) : buffer)
      .then(buffer => options.optipng ? optipng(buffer) : buffer)
      .then(buffer => options.zopflipng ? zopflipng(buffer) : buffer);
  } else if (isGif(buffer)) {
    return Promise.resolve(buffer)
      .then(buffer => options.gifsicle ? gifsicle(buffer) : buffer);
  } else if (isSvg(buffer)) {
    return Promise.resolve(buffer)
      .then(buffer => options.svgo ? svgo(buffer, options.svgo) : buffer);
  }

  return Promise.resolve(buffer);
};

